RecognizeSpeech.input_schema:
{
                    title: AsrInputSchema,
                    type: object,
                    properties: {
                        CallSid:{ type:string },
                        callback_url_base:{ type:string },
                        mediaURIOrTextTTS: {
                            type: string
                        },    
                        acceptedInputType :{
                            type : string
                        },
                        hints :{
                            type : string
                        },
                        loop : {
                            type: string
                        },
                        useTextTTS : {
                            type: string
                        },
                        numberOfDigits: {
                            type: string
                        },
                        terminationKey: {
                            type: string
                        },
                        timeout: {
                            type: string
                        },
                        voice: {
                            type: string
                        }
                    }
                }

